import React from "react";
import Table from "antd/lib/table";
import "antd/lib/table/style/css";
import Button from "antd/lib/button";
import "antd/lib/button/style/css";

import { actionService } from "./eventService";
import { StatusTag } from "../StatusTag";

const EventsTable = ({ eventsData }) => {
  const tableColumns = [
    {
      title: "ID",
      dataIndex: "key",
      key: "id"
    },
    {
      title: "Title",
      dataIndex: "title",
      key: "title"
    },
    {
      title: "Level",
      dataIndex: "fileType",
      key: "fileType"
    },
    {
      title: "Duration",
      dataIndex: "process",
      key: "process"
    },
    {
      title: "Instructor",
      dataIndex: "performedBy",
      key: "performedBy"
    },
    {
      title: "Starting Date",
      dataIndex: "updatedAt",
      key: "updatedAt"
    },
    {
      title: "Status",
      key: "status",
      render: (text, record) => <StatusTag status={record.status} />
    }
  ];

  const handleAction = currentEvent => {
    actionService(currentEvent);
  };

  return <Table dataSource={eventsData} columns={tableColumns} />;
};

export { EventsTable };
