import React from "react";
import {
  Form,
  InputGroup,
  InputGroupAddon,
  InputGroupText,
  FormInput,
  Dropdown,
  DropdownItem,
  DropdownToggle,
  DropdownMenu
} from "shards-react";

export default class SearchBar extends React.Component {
  constructor(props) {
    super(props);
    this.toggle = this.toggle.bind(this);
    this.state = {
      open: false
    };
  }

  toggle() {
    this.setState({ open: !this.state.open });
  }


  render(){
    return(
      <Form className="main-navbar__search w-100 h-80 d-none d-md-flex d-lg-flex">
        <InputGroup>
        <FormInput />
          <Dropdown
            addonType="append"
            open={this.state.open}
            toggle={this.toggle}
          >
            <DropdownToggle caret>Dropdown</DropdownToggle>
            <DropdownMenu right>
              <DropdownItem>Action</DropdownItem>
              <DropdownItem>Another action</DropdownItem>
              <DropdownItem>Something else here</DropdownItem>
            </DropdownMenu>
          </Dropdown>
        </InputGroup>
      </Form>
    );
  }
}
